    private void calcBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcBtnActionPerformed
        
        int value1 = Integer.parseInt(jValue1.getText());
        int value2 = Integer.parseInt(jValue2.getText());
        int value3 = Integer.parseInt(jValue3.getText());
        
        int qty1 = Integer.parseInt(jQty1.getText());
        int qty2 = Integer.parseInt(jQty2.getText());
        int qty3 = Integer.parseInt(jQty3.getText());
        
        int total1 = value1*qty1;
        int total2 = value2*qty2;
        int total3 = value3*qty3;
        
        jTotal1.setText("$"+total1);
        jTotal2.setText("$"+total2);
        jTotal3.setText("$"+total3);
        
        int total = total1+total2+total3;
        jTotal.setText("$"+total);
        
        String name = nameFld.getText();
        jNmrslt.setText("Dear " + name + ", the total of your purchase is: $" + total);
    }//GEN-LAST:event_calcBtnActionPerformed

